  def readIds(fn:String):Array[String] = {
    val ids = new ArrayBuffer[String]()
    for(line <- Source.fromFile(fn).getLines()) {
      var l = line.trim
      if (! l.startsWith("PMC"))
        l = "PMC" + l
      ids += l
    }
    ids.toArray
  }
